// Generated by Kiro - Main entry point for StoryAgent service
import dotenv from 'dotenv';
import { StoryAgentServer } from './server';

// Load environment variables
dotenv.config();

// Validate required environment variables
const GROQ_API_KEY = process.env.GROQ_API_KEY || process.env.HUGGINGFACE_API_KEY;
const PORT = parseInt(process.env.PORT || '3001', 10);

if (!GROQ_API_KEY) {
  console.error('❌ Error: GROQ_API_KEY environment variable is required');
  console.error('Get your free API key from: https://console.groq.com/keys');
  process.exit(1);
}

console.log('✅ Using Groq API for story generation (FREE, super fast!)');

// Create and start server
const server = new StoryAgentServer(GROQ_API_KEY, PORT);
server.start();

// Graceful shutdown
process.on('SIGTERM', () => {
  console.log('SIGTERM signal received: closing HTTP server');
  process.exit(0);
});

process.on('SIGINT', () => {
  console.log('SIGINT signal received: closing HTTP server');
  process.exit(0);
});

// Export for testing
export { StoryAgentServer };
export { StoryService } from './story.service';
export * from './types';
