// Generated by Kiro - Main entry point for StoryAgent service
import dotenv from 'dotenv';
import { StoryAgentServer } from './server';

// Load environment variables
dotenv.config();

// Validate required environment variables
const OPENAI_API_KEY = process.env.OPENAI_API_KEY;
const PORT = parseInt(process.env.PORT || '3002', 10);

if (!OPENAI_API_KEY) {
  console.error('âŒ Error: OPENAI_API_KEY environment variable is required');
  process.exit(1);
}

// Create and start server
const server = new StoryAgentServer(OPENAI_API_KEY, PORT);
server.start();

// Graceful shutdown
process.on('SIGTERM', () => {
  console.log('SIGTERM signal received: closing HTTP server');
  process.exit(0);
});

process.on('SIGINT', () => {
  console.log('SIGINT signal received: closing HTTP server');
  process.exit(0);
});

// Export for testing
export { StoryAgentServer };
export { StoryService } from './story.service';
export * from './types';
