#!/bin/bash

# Run All Tests Script
# Generated by Kiro - HauntedAI Platform

echo "üß™ HauntedAI - Running All Tests"
echo "=================================="
echo ""

# Colors
GREEN='\033[0;32m'
RED='\033[0;31m'
YELLOW='\033[1;33m'
BLUE='\033[0;34m'
NC='\033[0m' # No Color

# Track results
TOTAL_PASSED=0
TOTAL_FAILED=0

# Function to run test and track results
run_test() {
    local test_name=$1
    local test_command=$2
    
    echo -e "${BLUE}Running: ${test_name}${NC}"
    
    if eval "$test_command" > /dev/null 2>&1; then
        echo -e "${GREEN}‚úì ${test_name} PASSED${NC}"
        ((TOTAL_PASSED++))
        return 0
    else
        echo -e "${RED}‚úó ${test_name} FAILED${NC}"
        ((TOTAL_FAILED++))
        return 1
    fi
}

# 1. Property-Based Tests
echo -e "\n${YELLOW}1. Property-Based Tests${NC}"
echo "------------------------"
run_test "Assets Property Tests" "npm test -- assets.property.test.ts --runInBand --no-coverage --silent"
run_test "Rooms Property Tests" "npm test -- rooms.property.test.ts --runInBand --no-coverage --silent"
run_test "Auth Property Tests" "npm test -- auth.property.test.ts --runInBand --no-coverage --silent"
run_test "Live Logging Property Tests" "npm test -- live-logging.property.test.ts --runInBand --no-coverage --silent"

# 2. Unit Tests
echo -e "\n${YELLOW}2. Unit Tests${NC}"
echo "------------------------"
run_test "Rooms SSE Unit Tests" "npm test -- rooms-sse.spec.ts --runInBand --no-coverage --silent"
run_test "Auth Service Unit Tests" "npm test -- auth.service.spec.ts --runInBand --no-coverage --silent"
run_test "Rooms Service Unit Tests" "npm test -- rooms.service.spec.ts --runInBand --no-coverage --silent"

# Summary
echo ""
echo "=================================="
echo -e "${BLUE}Test Results Summary${NC}"
echo "=================================="
echo -e "Total Tests: $((TOTAL_PASSED + TOTAL_FAILED))"
echo -e "${GREEN}‚úì Passed: ${TOTAL_PASSED}${NC}"
echo -e "${RED}‚úó Failed: ${TOTAL_FAILED}${NC}"

if [ $TOTAL_FAILED -eq 0 ]; then
    echo -e "\n${GREEN}üéâ All Tests Passed! üéâ${NC}"
    exit 0
else
    echo -e "\n${YELLOW}‚ö†Ô∏è  Some Tests Failed${NC}"
    exit 1
fi
