// Generated by Kiro
// Redis service for pub/sub messaging
// Managed by Kiro - Part of HauntedAI SSE implementation
import { Injectable, OnModuleInit, OnModuleDestroy, Logger } from '@nestjs/common';
import Redis from 'ioredis';

import { AgentLog } from './types/agent-log.types';

@Injectable()
export class RedisService implements OnModuleInit, OnModuleDestroy {
  private readonly logger = new Logger(RedisService.name);
  private publisher!: Redis;
  private subscriber!: Redis;

  async onModuleInit() {
    const redisUrl = process.env.REDIS_URL || 'redis://localhost:6379';
    
    this.publisher = new Redis(redisUrl, {
      retryStrategy: (times: number) => {
        const delay = Math.min(times * 50, 2000);
        return delay;
      },
    });

    this.subscriber = new Redis(redisUrl, {
      retryStrategy: (times: number) => {
        const delay = Math.min(times * 50, 2000);
        return delay;
      },
    });

    this.publisher.on('connect', () => {
      this.logger.log('Redis publisher connected');
    });

    this.publisher.on('error', (err: Error) => {
      this.logger.error('Redis publisher error:', err);
    });

    this.subscriber.on('connect', () => {
      this.logger.log('Redis subscriber connected');
    });

    this.subscriber.on('error', (err: Error) => {
      this.logger.error('Redis subscriber error:', err);
    });
  }

  async onModuleDestroy() {
    await this.publisher.quit();
    await this.subscriber.quit();
  }

  /**
   * Publish a log message to a room channel
   * Requirements: 5.1
   */
  async publishLog(roomId: string, log: AgentLog): Promise<void> {
    const channel = `room:${roomId}:logs`;
    const message = JSON.stringify({
      ...log,
      timestamp: log.timestamp.toISOString(),
    });

    try {
      await this.publisher.publish(channel, message);
      this.logger.debug(`Published log to ${channel}`);
    } catch (error) {
      this.logger.error(`Failed to publish log to ${channel}:`, error);
      throw error;
    }
  }

  /**
   * Subscribe to room logs
   * Requirements: 5.1, 5.2
   */
  async subscribeToRoomLogs(
    roomId: string,
    callback: (log: AgentLog) => void,
  ): Promise<() => Promise<void>> {
    const channel = `room:${roomId}:logs`;
    
    // Create a new subscriber instance for this subscription
    const subscriber = this.subscriber.duplicate();
    
    await subscriber.subscribe(channel);
    
    subscriber.on('message', (ch: string, message: string) => {
      if (ch === channel) {
        try {
          const log = JSON.parse(message);
          // Convert timestamp string back to Date
          log.timestamp = new Date(log.timestamp);
          callback(log);
        } catch (error) {
          this.logger.error('Failed to parse log message:', error);
        }
      }
    });

    this.logger.debug(`Subscribed to ${channel}`);

    // Return unsubscribe function
    return async () => {
      await subscriber.unsubscribe(channel);
      await subscriber.quit();
      this.logger.debug(`Unsubscribed from ${channel}`);
    };
  }

  /**
   * Get Redis publisher instance for direct access
   */
  getPublisher(): Redis {
    return this.publisher;
  }

  /**
   * Get Redis subscriber instance for direct access
   */
  getSubscriber(): Redis {
    return this.subscriber;
  }
}
